mapper.select:
select a.preordertype,a.preorderid,a.preorderprice, b.docloginid,c.docloginpix,b.docname,b.doctitle,b.dochosp,b.hosplevel,b.docprimarydept,b.docseconddept, b.docallday,b.docexpert, d.docname as redocname,d.doctitle as redoctitle, e.preordertypename
from preorder as a left join doctorinfo as b on a.preOrderDocLoginId=b.docLoginId left join doctorlogininfo as c on a.preOrderDocLoginId = c.docLoginId left join doctorinfo as d on a.preOrderReDocLoginId = c.docLoginId left join preordertype as e on a.preOrderType=e.preOrderTypeId where a.userSickId =#{0}
and a.preOrderType=#{1}

 	 select * 
	 from preorder
	 where preOrderDocLoginId = #{0} and userSickId=#{1}

 	 select * 
	 from preorder
	 where preOrderDocLoginId = #{0} and userSickId = #{1} and preOrderType=#{2}

 	 select * 
	 from preorder
	 where preOrderDocLoginId = #{0}  and preOrderType=#{1}
