mapper.select:
select a.userorderid,a.userorderstateid,a.userorderptime,a.userorderrtime,a.userorderetime,a.userorderdprice, a.userorderhprice,a.userorderprice,c.hospname, e.usersickdesc,e.usersickpic,f.familyname,f.familymale,f.familyage from userorder as a left join doctorinfo as b on a.userOrderDocLoginId=b.docLoginId left join hospinfo as c on a.userOrderHospId=c.hospLoginId left join doctorlogininfo as d on a.userOrderDocLoginId= d.docLoginId left join usersick as e on a.userSickId = e.userSickId left join familyinfo as f on e.familyId=f.familyId where a.userOrderDocLoginId=#{0}
and a.userOrderStateId=#{1}
ORDER BY a.userorderptime DESC
